import React, { useState } from 'react';
import { questions } from './questions';
import M from 'materialize-css';
import history from './history'
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { animationSlideIn, transition } from './animations'

const Quiz = () => {
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const [score, setScore] = useState(0);

    const [fiftyFifty, setFiftyFifty] = useState(2);
    const [usedFiftyFifty, setUsedFiftyFifty] = useState(false);

    const handelAnswerBtnClick = (isCorrect) => {
        if(isCorrect){
        correctAnswer()		
        setScore(score + 1)
        } else wrongAnswer()
        const nextQuestion = currentQuestion + 1;
        if(nextQuestion < questions.length){
        setCurrentQuestion(nextQuestion)
        } else {
        setShowScore(true)
        }
    }

    const handleResetBtnClick = () => {
        setCurrentQuestion(0)
        setShowScore(false)
        setScore(0)
        history.push('/')
    }

    const correctAnswer = () => {
        M.toast({
            html: 'Correct Answer!',
            classes: 'toast-valid',
            displayLength: 1500
        });
    }

    const wrongAnswer = () => {
        M.toast({
            html: 'Wrong Answer!',
            classes: 'toast-invalid',
            displayLength: 1500
        });
    }

    const onClick5050 = () => {
        if(fiftyFifty > 0 && usedFiftyFifty === false){
            const options = document.querySelectorAll('.option');
            const randomNums = [];
            let indexOfAnswer;
            options.forEach((option,index) => {
                if(option.innerHTML.toLowerCase() === answer.toLowerCase()){
                    indexOfAnswer = index;
                }
            });
            let count = 0;
            do{
                const randomNum = Math.round(Math.random() * 3);
                if(randomNum !== indexOfAnswer){
                    if(randomNums.length < 2 && !randomNums.includes(randomNum) && !randomNums.includes(indexOfAnswer)){
                        randomNums.push(randomNum);
                        count ++;
                    } else { 
                        while (true){
                            const newRandomNum = Math.round(Math.random() * 3);
                            if(!randomNums.includes(newRandomNum) && !randomNums.includes(indexOfAnswer)){
                                randomNums.push(newRandomNum);
                                count ++;
                                break;
                            }
                        }
                    }
                }
            } while(count < 2)
            options.forEach((option, index) => {
                if(randomNums.includes(index)){
                    option.style.visibility = 'hidden'
                }
            });
            setFiftyFifty(i => i - 1);
            setUsedFiftyFifty(true)
        }
    }

        return (
            <motion.div 
                className='app-container'   
                initial='out'
                animate='end'
                exit='out'
                variants={animationSlideIn}
                transition={transition}
            >
                {showScore ? (
                    <div className='score-section'>
            You scored {score} out of {questions.length}
            <Link to='/' ><button onClick={handleResetBtnClick} className='reset-btn'>Reset</button></Link>
            </div>
                ) : (
                    <>
                        <div className='question-section'>
                            <div className='question-text'>{questions[currentQuestion].questionText}</div>
                            <div className='question-count'>
                                <span>Question {currentQuestion + 1}/{questions.length}</span>
                                <span onClick={onClick5050}>50/50</span>
                            </div>
                        </div>
                        <div className='answer-section'>
                            {questions[currentQuestion].answerOptions.map(answer => (
                            <button className="option" onClick={()=>handelAnswerBtnClick(answer.isCorrect)}>{answer.answerText}</button>
                            ))}
                        </div>
                    </>
                )}
            </motion.div>
        );
    }

    export default Quiz;
