import React, { useState } from 'react';
import { questions } from './questions';
import M from 'materialize-css';
import history from './history'
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { animationSlideIn, transition } from './animations'
import Marquee from './Marquee';


const Quiz = () => {
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const [score, setScore] = useState(0);

    const handelAnswerBtnClick = (isCorrect) => {
        if(isCorrect){
        correctAnswer()		
        setScore(score + 1)
        } else wrongAnswer()
        const nextQuestion = currentQuestion + 1;
        if(nextQuestion < questions.length){
        setCurrentQuestion(nextQuestion)
        } else {
        setShowScore(true)
        }
    }

    const handleResetBtnClick = () => {
        setCurrentQuestion(0)
        setShowScore(false)
        setScore(0)
        history.push('/')
    }

    const correctAnswer = () => {
        M.toast({
            html: 'Correct Answer!',
            classes: 'toast-valid',
            displayLength: 1500
        });
    }

    const wrongAnswer = () => {
        M.toast({
            html: 'Wrong Answer!',
            classes: 'toast-invalid',
            displayLength: 1500
        });
    }

        return (
            <motion.div 
                className='app-container'   
                initial='out'
                animate='end'
                exit='out'
                variants={animationSlideIn}
                transition={transition}
            >
                {showScore ? (
                    <div className='score-section'>
            You scored {score} out of {questions.length}
            <Link to='/' ><button onClick={handleResetBtnClick} className='reset-btn'>Reset</button></Link>
            </div>
                ) : (
                    <>
                        <div className='question-section'>
                            <div className='question-text'>{questions[currentQuestion].questionText}</div>
                            <div className='question-count'>
                                <span>Question {currentQuestion + 1}</span>/{questions.length}
                            </div>
                        </div>
                        <div className='answer-section'>
                {questions[currentQuestion].answerOptions.map(answer => (
                <button onClick={()=>handelAnswerBtnClick(answer.isCorrect)}>{answer.answerText}</button>
                ))}
                        </div>
                    </>
                )}
            </motion.div>
        );
    }

    export default Quiz;
